import React from "react";

const Header = ({ categories }) => {
  return (
    <div style={{ maxWidth: "100%" }}>
      <div className="flex items-center justify-between text-white py-4 px-6">
        {/* Logo and Company Name */}
        <div className="flex items-center space-x-4">
          <div>
            <svg
              width="165"
              height="40"
              viewBox="0 0 165 40"
              fill="none"
              xmlns="http://www.w3.org/2000/svg"
            >
              {/* SVG paths */}
            </svg>
          </div>
        </div>

        {/* Categories */}
        <div className="flex items-center space-x-4 overflow-x-auto">
          {categories.map((category, index) => (
            <a
              key={category}
              href="#"
              className="hover:text-gray-800"
              style={{ color: "#657178" }}
            >
              {category}
            </a>
          ))}
          <div className="relative">
            <button className="hover:text-gray-300">More</button>
            <div className="absolute bg-white w-48 shadow-lg rounded mt-2 opacity-0 invisible group-hover:opacity-100 group-hover:visible">
              {categories.slice(4).map((category, index) => (
                <a
                  key={category}
                  href="#"
                  className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
                >
                  {category}
                </a>
              ))}
            </div>
          </div>
        </div>

        {/* Social Icons and Search Bar */}
        <div className="flex items-center space-x-4">
          {/* Social Icons */}
          {/* Search Bar */}
        </div>
      </div>
    </div>
  );
};

export default Header;
