import "swiper/css";
import SwiperCore, { Swiper, SwiperSlide } from "swiper";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import styles from "./Slider.module.css";

SwiperCore.use([Pagination, Navigation]);

const SliderContent = [
  // Your slider content data array
];

function SwiperSlider() {
  return (
    <Swiper
      style={{
        "--swiper-navigation-color": "#fff",
        "--swiper-pagination-color": "#fff",
      }}
      lazy={true}
      pagination={{
        clickable: true,
      }}
      navigation={{
        nextEl: ".custom-next",
        prevEl: ".custom-prev",
      }}
      className={styles.mySwiper}
      spaceBetween={30}
    >
      {SliderContent.map((slide, index) => (
        <SwiperSlide key={index}>
          <img
            style={{ borderRadius: "20px" }}
            src={slide.img}
            loading="lazy"
            alt={`Slide ${index + 1}`}
          />
          <div className="swiper-lazy-preloader swiper-lazy-preloader-white"></div>
          <div className={styles.sliderContent}>
            <div className="flex align-center">
              <p>{slide.date}</p>
              <p>{slide.tag}</p>
            </div>
            <p
              className="text-24 text-main leading-40"
              style={{ fontSize: "24px" }}
            >
              {slide.text}
            </p>
            <p className="text-16 text-teaser">{slide.teaser}</p>
          </div>
        </SwiperSlide>
      ))}
      <div className="custom-prev" style={{ left: "10px", top: "50%" }}>
        {/* Your previous button SVG */}
      </div>
      <div className="custom-next" style={{ right: "10px", top: "50%" }}>
        {/* Your next button SVG */}
      </div>
    </Swiper>
  );
}

function ButtonSlider() {
  return (
    <div className="flex justify-center items-center">
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2">
        Previous
      </button>
      <img
        src="https://swiperjs.com/demos/images/nature-1.jpg"
        alt="Nature 1"
      />
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded ml-2">
        Next
      </button>
    </div>
  );
}

function Slider({ type }) {
  return (
    <div className="container mx-auto">
      {type === "swiper" ? <SwiperSlider /> : <ButtonSlider />}
    </div>
  );
}

export default Slider;
