import React, { useState } from "react";

const Header = ({ categories }) => {
  const [isDropdownVisible, setIsDropdownVisible] = useState(false);

  return (
    <div className="flex items-center justify-between text-white py-4 px-6">
      {/* Logo and Company Name */}
      <div className="flex items-center space-x-4">
        <div>
          <svg
            width="165"
            height="40"
            viewBox="0 0 165 40"
            fill="none"
            xmlns="http://www.w3.org/2000/svg"
          >
            {/* Your SVG code */}
          </svg>
        </div>
      </div>

      {/* Categories */}
      <div className="flex items-center space-x-4">
        {categories.slice(0, 4).map((category, index) => (
          <React.Fragment key={category}>
            <a
              href="#"
              className="hover:text-gray-800"
              style={{ color: "#657178" }}
            >
              {category}
            </a>
            {index < 3 && <div style={{ width: "32px" }} />}
          </React.Fragment>
        ))}
        <div
          className="relative"
          style={{ marginLeft: "32px" }}
          onMouseEnter={() => setIsDropdownVisible(true)}
          onMouseLeave={() => setIsDropdownVisible(false)}
        >
          <button style={{ color: "#657178" }} className="hover:text-gray-300">
            More
          </button>
          {isDropdownVisible && (
            <div className="absolute bg-white w-48 shadow-lg rounded mt-2">
              <a
                href="#"
                className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
              >
                Category 5
              </a>
              <a
                href="#"
                className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
              >
                Category 6
              </a>
              {/* Add more categories here */}
            </div>
          )}
        </div>
      </div>

      {/* Social Icons and Search Bar */}
      <div
        className="flex items-center space-x-4"
        style={{ marginLeft: "120px" }}
      >
        {/* Your social icons and search bar */}
      </div>
    </div>
  );
};

export default Header;
