import React, { useState, useEffect } from "react";
import ScrollComponent from "../../UI/ScrollComponent";
import styles from "./News.module.css";

const News = () => {
  const [scrollContent, setscrollContent] = useState([]);
  const content = [
    {
      type: "scroll",
      time: "7:30",
      span: "02.01.2023",
      text: "დღეიდან ასაწონად განკუთვნილი პოლიეთილენის პარკები აიკრძალა",
    },
    // Add more news items here as needed
  ];

  const determinescrollContent = () => {
    const screenWidth = window.innerWidth;
    if (screenWidth >= 1900) {
      setscrollContent(content.slice(0, 4));
    } else if (screenWidth < 1023) {
      setscrollContent(content.slice(0, 3));
    } else {
      setscrollContent(content);
    }
  };

  useEffect(() => {
    // Set initial displayed content based on window width
    determinescrollContent();

    // Add event listener for window resize
    window.addEventListener("resize", determinescrollContent);

    // Clean up event listener on component unmount
    return () => {
      window.removeEventListener("resize", determinescrollContent);
    };
  }, []);

  return (
    <div className={styles.news}>
      <h1>ყველა სიახლე</h1>
      <div className={styles.scrollContainer}>
        {/* Pass scrollContent instead of content */}
        <ScrollComponent type="yey" content={scrollContent} />
      </div>
    </div>
  );
};

export default News;
