import React from "react";

const Header = ({ companyName, categories, socialMedia }) => {
  return (
    <div className="flex items-center justify-between bg-gray-800 text-white py-4 px-6">
      {/* Logo and Company Name */}
      <div className="flex items-center space-x-4">
        <img src="/logo.png" alt="Company Logo" className="h-8 w-auto" />
        <span className="text-lg font-bold">{companyName}</span>
      </div>

      {/* Categories */}
      <div className="flex space-x-4">
        {categories.slice(0, 4).map((category) => (
          <a key={category} href="#" className="hover:text-gray-300">
            {category}
          </a>
        ))}
        <div className="relative">
          <button className="hover:text-gray-300">More</button>
          <div className="absolute bg-white w-48 shadow-lg rounded mt-2">
            <a
              href="#"
              className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
            >
              Category 5
            </a>
            <a
              href="#"
              className="block px-4 py-2 text-gray-800 hover:bg-gray-200"
            >
              Category 6
            </a>
            {/* Add more categories here */}
          </div>
        </div>
      </div>

      {/* Social Icons and Search Bar */}
      <div className="flex items-center space-x-4">
        {socialMedia.map(({ name, icon }) => (
          <img key={name} src={`/${icon}`} alt={name} className="h-6 w-6" />
        ))}
        <input
          type="text"
          placeholder="Search"
          className="px-3 py-1 rounded-md bg-gray-700 text-white focus:outline-none"
        />
      </div>
    </div>
  );
};

export default Header;
