import "swiper/css";
import SwiperCore from "swiper";
import { Swiper, SwiperSlide } from "swiper/react";
import "swiper/css/pagination";
import "swiper/css/navigation";
import { Pagination, Navigation } from "swiper/modules";
import styles from "./Slider.module.css";

SwiperCore.use([Pagination, Navigation]);

const SliderContent = [
  {
    img: "/images/pic.jpeg",
    date: "12.08.2023",
    tag: "პოლიტიკა",
    text: "ქვემო ქართლის რეგიონში 20 მლნ ლარამდე ღირებულების ინფრასტრუქტურული პროექტები დაიწყება",
    teaser:
      "განთავსების (ლისტინგის) ორგანიზატორი სომხეთის ბანკი სს “არდშიბანკია”. ობლიგაციის ნომინალი - 1000 USD",
  },
];

function SwiperSlider() {
  return (
    <Swiper
      style={{
        "--swiper-navigation-color": "#fff",
        "--swiper-pagination-color": "#fff",
      }}
      lazy={true}
      pagination={{
        clickable: true,
      }}
      navigation={true}
      className={styles.mySwiper}
    >
      <SwiperSlide>
        <img
          src="https://swiperjs.com/demos/images/nature-1.jpg"
          loading="lazy"
          alt="Nature 1"
        />
        <div className="swiper-lazy-preloader swiper-lazy-preloader-white"></div>
        <div className={styles.sliderContent}>
          hahahslnodh ahd
          <p>bsdkabsfkb</p>
        </div>
      </SwiperSlide>
      <SwiperSlide>
        <img
          src="https://swiperjs.com/demos/images/nature-2.jpg"
          loading="lazy"
          alt="Nature 2"
        />
        <div className="swiper-lazy-preloader swiper-lazy-preloader-white"></div>
      </SwiperSlide>
      <SwiperSlide>
        <img
          src="https://swiperjs.com/demos/images/nature-3.jpg"
          loading="lazy"
          alt="Nature 3"
        />
        <div className="swiper-lazy-preloader swiper-lazy-preloader-white"></div>
      </SwiperSlide>
    </Swiper>
  );
}

function ButtonSlider() {
  return (
    <div className="flex justify-center items-center">
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded mr-2">
        Previous
      </button>
      <img
        src="https://swiperjs.com/demos/images/nature-1.jpg"
        alt="Nature 1"
      />
      <button className="bg-blue-500 hover:bg-blue-700 text-white font-bold py-2 px-4 rounded ml-2">
        Next
      </button>
    </div>
  );
}

function Slider({ type }) {
  return (
    <div className="container mx-auto">
      {type === "swiper" ? <SwiperSlider /> : <ButtonSlider />}
    </div>
  );
}

export default Slider;
