import React from "react";

const ScrollComponent = ({ content }) => {
  return (
    <div style={{ overflowY: "scroll", height: "300px" }}>
      {content.map((item, index) => (
        <div key={index} style={{ marginBottom: "20px" }}>
          <h3>{item.time}</h3>
          <p>{item.text}</p>
        </div>
      ))}
    </div>
  );
};

export default ScrollComponent;
